    //dp(i) means the longest valid parentheses that ends with ith index
    // initializing dp(i)=dp(i-2)+2
    // dp(i)=dp(i-2)+2 if dp(i-1)='('  else dp(i-1)+2
    public int longestValidParentheses4(String s) {
        if(s==null||s.length()==0) return 0;
        int L=s.length(), max=0;
        int[] dp=new int[L];
        for (int i = 1; i < L; i++) {
            if(s.charAt(i)==')'){
                if(s.charAt(i-1)==')'){
                    if(i-dp[i-1]==1 && s.charAt(0)=='(')
                        dp[i]=dp[i-1]+2;
                    if(i-dp[i-1]>1 && s.charAt(i-dp[i-1]-1)=='(')
                        dp[i]=dp[i-1]+dp[i-dp[i-1]-2]+2;
                }else {
                    dp[i]=(i>=2?dp[i-2]:0)+2;
                }
            }
            max=Math.max(max, dp[i]);
        }
        return max;
    }

    //using stack
    public int longestValidParentheses3(String s) {
        int[] stack=new int[s.length()+1];
        int top=0;
        stack[top++]=-1;
        int max=0, j=-1;
        while (++j<s.length()){
            if(s.charAt(j)=='(') {
                stack[top++]=j;
            } else {
                if(top==1) {
                    stack[top-1]=j;
                    continue;
                }
                top--;
                max = Math.max(max, j - stack[top-1]);
            }
        }
        return max;
    }

    //O(N) simulate stack
    public int longestValidParentheses2(String s) {
        Integer[] top=new Integer[s.length()+1];
        int max=0, stacksize=0, j=-1;
        while (++j<s.length()){
            if(s.charAt(j)=='(') {
                stacksize++;
                if(top[stacksize]==null)
                    top[stacksize] = j;
            } else {
                if(stacksize==0){
                    top[stacksize+1]=null;
                    continue;
                }
                max=Math.max(max, j-top[stacksize]+1);
                top[stacksize+1]=null;
                stacksize--;
            }
        }
        return max;
    }

    //O(N^2) brute force  TLE
    public int longestValidParentheses1(String s) {
        Stack<Character> stack=new Stack<>();
        int max=0;
        for (int i = 0; i < s.length(); i++) {
            if(s.length()-i<=max)  break;
            stack.clear();
            int j=i-1;
            while ((++j)<s.length()){
                if(s.charAt(j)=='(') stack.push('(');
                else {
                    if(stack.empty()) break;
                    stack.pop();
                }
                if(stack.empty())
                    max=Math.max(max, j-i+1);
            }
        }
        return max;
    }

    //dp(i,j)=true if dp(i+1,j-1) is valid and s(i)='(' and s(j)=')'   or  dp(i,k) is true and dp(k+1,j) is true (i<k<j-1)
    //O(N^3) TLE DP
    public int longestValidParentheses(String s) {
        if(s==null||s.length()==0) return 0;
        int L=s.length();
        boolean[][] dp=new boolean[L][L];
        char[] arr=s.toCharArray();
        char lp='(', rp=')';
        int max=0;
        for (int i = 0; i < L-1; i++)
            if(arr[i]==lp&&arr[i+1]==rp) {
                dp[i][i + 1] = true;
                max=2;
            }
        for (int d = 3; d < L; d+=2) {
            for (int i = 0; i < L-d; i++) {
                int j=i+d;
                if(dp[i+1][j-1] && arr[i]==lp&&arr[j]==rp) {
                    dp[i][j] = true;
                } else{
                    for (int k = i+1; k < j-1; k+=2)
                        if(dp[i][k]&&dp[k+1][j]) {
                            dp[i][j]=true;
                            break;
                        }
                }
                if(dp[i][j]) max=j-i+1;
            }
        }
        return max;
    }
