    //O(SN)
    public boolean wordBreak(String s, List<String> wordDict) {
        Map<Integer, List<Integer>> map=new HashMap<>();
        for(String str:wordDict) {
            List<Integer> idxs=Utils.allOccurence(s, str);
            if(idxs.size()==0) break;
            for(int idx:idxs) {
                if (!map.containsKey(idx)) map.put(idx, new ArrayList<>());
                map.get(idx).add(idx + str.length());
            }
        }
        return dfs(map, 0, s.length());
    }
    boolean dfs(Map<Integer, List<Integer>> map, int idx, int end){
        if(idx==end) return true;
        if(!map.containsKey(idx)) return false;
        for(int i : map.get(idx)){
            if(dfs(map, i, end)) return true;
        }
        return false;
    }


