    public boolean wordBreak1(String s, List<String> wordDict) {
        if(wordDict.isEmpty()) return false;
        boolean[] dp=new boolean[s.length()+1];
        dp[0]=true;
        for (int i = 1; i <= s.length(); i++) {
            for (int j = 0; j < i; j++) {
                if(dp[j] && wordDict.contains(s.substring(j, i))) dp[i]=true;
            }
        }
        return dp[s.length()];
    }

    public boolean wordBreak(String s, List<String> wordDict) {
        if(s.equals("") && wordDict.contains(s)) return true;
        List<int[]> list=new ArrayList<>();
        for(String str:wordDict) {
            List<Integer> idxs=Utils.allOccurence(s, str);
            for(int idx:idxs)
                list.add(new int[]{idx, idx+str.length()});
        }
        if(list.isEmpty()) return false;
        list.sort((a,b)->a[0]-b[0]!=0?a[0]-b[0]:a[1]-b[1]);
        if(list.get(0)[0]!=0) return false;
        boolean[] dp=new boolean[s.length()+1];
        dp[0]=true;
        for(int[] p:list)
            if(dp[p[0]]) dp[p[1]] =true;
        return dp[s.length()];
    }
